/**
 * DOMPurify (version simplifiée pour démo)
 * Nettoyage HTML pour sécurité XSS
 */

window.DOMPurify = {
    /**
     * Nettoie le HTML pour éviter les attaques XSS.
     */
    sanitize: function(dirty) {
        if (!dirty) return '';
        
        // Nettoyage basique - en production utiliser la vraie librairie
        return dirty
            .replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '')
            .replace(/javascript:/gi, '')
            .replace(/on\w+\s*=/gi, '');
    }
};

console.log('DOMPurify (version simplifiée) chargé');